import Header from './Header';

class SleepForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: '0'};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('You recorded ' + this.state.value + 'of sleep last night');
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            How many hours of sleep did you have?
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="1 hour(s)">1 hour(s)</option>
              <option value="1 hour(s)">3 hour(s)</option>
              <option value="5">5 hour(s)</option>
              <option value="7">7 hour(s)</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>
      );
    }
  }
  export default SleepForm;
